import React, { Fragment, useContext, useEffect} from 'react'
import AlertContext from '../../contexts/alert/alertContext';
import UserContext from '../../contexts/user/userContext';
import ProfileContext from '../../contexts/profile/profileContext';

const Profile = ({ match }) => {
    const  profileContext = useContext(ProfileContext);
    const  alertContext = useContext(AlertContext);
    const  userContext = useContext(UserContext);

    const { getProfile, profile, error, success, clearMessage } = profileContext;
    const { setAlert } = alertContext;
    const { loadUser, user} = userContext;

    useEffect(() => {
        loadUser();
        getProfile(match.params.userid);
        if(error) {
            setAlert(error, 'danger');
            clearMessage();
        }
        if(success) {
            setAlert(success, 'success');
            clearMessage();
        }
        // eslint-disable-next-line
    }, [error, success]);

    const renderProfile = (
        <Fragment>
            <p>{profile && profile.data.online ? "online" : ("offline, last login: " + (profile && profile.data.last_login))}</p>
            <p>Fame: {profile && profile.data.fame}</p>
            <p>Fristname: {profile && profile.data.firstname}</p>
            <p>Lastname: {profile && profile.data.lastname}</p>
            <p>Birthday: {profile && profile.data.birthday}</p>
            <p>Biography: {profile && profile.data.biography}</p>
        </Fragment>
    )

    return (
        <div>
           {profile? renderProfile: <p>no profile</p>}
        </div>
    )
}

export default Profile
