import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    CLEAR_MESSAGE,
    LOGIN_SUCCESS
} from '../types';


export default (state, action) => {
    switch (action.type) {
        case REGISTER_SUCCESS:
            return{
                ...state,
                success: action.payload,
                loading: false
            };
        case REGISTER_FAIL:
            return {
                ...state,
                error: action.payload,
                loading: false
            }
        case CLEAR_MESSAGE:
            return {
                ...state,
                error: null,
                success: null
            }
        case LOGIN_SUCCESS:
            return {

            }
        default:
            return state;
    }
}
