import {
    REGISTER_SUCCESS
} from '../types';


export default (state, action) => {
    switch (action.type) {
        case SEARCH_USERS:
            return{
                ...state,
                users: action.payload,
                loading: false
            };
        case SET_LOADING:
           return{
               ...state,//copy state
               loading: true
           };
        case CLEAR_USERS:
            return{
                ...state,
                users:[],
                loading: false
            };
        case GET_USER:
            return{
                ...state,
                user: action.payload,
                loading: false
            };
        case GET_REPOS:
            return{
                ...state,
                repos: action.payload,
                loading: false
            };
        default:
            return state;
    }
}
