import React,{ Fragment, useState, useContext, useEffect }  from 'react';
import UserContext from '../../contexts/user/userContext';
import { EDITACCOUNT } from '../../contexts/types';

const Account = () => {
  const userContext = useContext(UserContext);
  const { user, error, success, editAccount } = userContext;

  const [edit, setEdit] = useState(false);

  useEffect(() => {
    userContext.loadUser();
    //eslint-disable-next-line
  }, []);

  const onClick = () => {
      setEdit(true);
  }

  const accountInfo = (
    <Fragment>
      <div className="form-group"> 
          <p>Email:</p> <p>{user && user.data.email}</p> 
          <p>Firstname:</p> <p>{user && user.data.firstname}</p> 
          <p>Lastnme:</p> <p>{user && user.data.lastname}</p>

          <button className="btn btn-primary btn-block" onClick={onClick}>Edit</button>
      </div>

    </Fragment>
  )

  // const onChange =(e) => {
  //   // const field = e.target.name;
  //   // user.data.field = e.target.value;
  //  console.log(e.target.value);
  // }

  const onSubmit =(e) => {
    e.preventDefault();
    console.log(user);
  }

const editForm = (
  <Fragment>
      <form onSubmit={onSubmit}>
          <div className="form-group">
              <label htmlFor="email">Email: </label>
			  <input type='email' name='email' 
				placeholder={user && user.data.email} 
				onChange={e => user.data.email = e.target.value} />
          </div>
          <div className="form-group">
              <label htmlFor="firstname">Firstname: </label>
			  <input type='text' name='firstname'
				placeholder={user && user.data.firstname}
				onChange={e => user.data.firstname = e.target.value} />
          </div>
          <div className="form-group">
              <label htmlFor="lastname">Lastname: </label>
			  <input type='text' name='lastname'
			  	placeholder={user && user.data.lastname} 
			  	onChange={e => user.data.lastname = e.target.value} />
          </div>
          <input type="submit" value="Comfirm" className="btn btn-primary btn-block" />
      </form>

  </Fragment>
)
  return (
      <div className='container'>
        { !edit ? accountInfo : editForm}
      </div>
  )
}

export default Account
