import React,{ useContext, useEffect }  from 'react'
import AuthContext from '../../contexts/auth/authContext';
const Account = () => {
    const authContext = useContext(AuthContext);
    const { user } = authContext;

  useEffect(() => {
    authContext.loadUser();
    //eslint-disable-next-line
  }, []);
    return (
        <div className='form-container'>
            <div>Email: <span className="capitalize">{user && user.data.email}</span></div>
            <div>Firstname: <span className="capitalize">{user && user.data.firstname}</span> </div>
            <div>Lastnme: <span className="capitalize">{user && user.data.lastname}</span></div>
        </div>
    )
}

export default Account
