import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
} from '../types';


export default (state, action) => {
    switch (action.type) {
        case REGISTER_SUCCESS:
            return{
                ...state,
                message: action.payload,
                loading: false
            };
        case REGISTER_FAIL:
            return {
                ...state,
                error: action.payload,
                loading: false
            }
        default:
            return state;
    }
}
