import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    CLEAR_MESSAGE,
    LOGIN_SUCCESS,
    LOGIN_FAIL
} from '../types';


export default (state, action) => {
    switch (action.type) {
        case REGISTER_SUCCESS:
            return{
                ...state,
                success: action.payload,
                loading: false
            };
        case REGISTER_FAIL:
        case LOGIN_FAIL:
            return {
                ...state,
                error: action.payload,
                loading: false
            }
        case CLEAR_MESSAGE:
            return {
                ...state,
                error: null,
                success: null
            }
        case LOGIN_SUCCESS:
            localStorage.setItem('token', action.payload.token);
            return {
                ...state,
                ...action.payload,
                isAuthenticated: true,
                loading: false
            }
        case LOGIN_FAIL:
            return {
                ...state,
                error: action.payload,
                loading: false
            }
        default:
            return state;
    }
}
