import React,{ Fragment, useState, useContext, useEffect }  from 'react'
import UserContext from '../../contexts/user/userContext';
import { EDITACCOUNT } from '../../contexts/types';

const Account = () => {
  const userContext = useContext(UserContext);
  const { user, error, success, editAccount } = userContext;

  const [edit, setEdit] = useState(false);
  const [account, setAccount] = useState({
    email: user.data.email,
    firstname: '',
    lastname: '',
})
  useEffect(() => {
    userContext.loadUser();
    //eslint-disable-next-line
  }, []);

  const onClick = () => {
      setEdit(true);
  }

  const onChange =() => {

  }

  const onSubmit =() => {

  }
  const accountInfo = (
    <Fragment>
          <p className="lead">Email: {user && user.data.email}</p> 
          <p className="lead">Firstname: {user && user.data.firstname}</p> 
          <p className="lead">Lastnme: {user && user.data.lastname}</p>
          <button className="btn btn-primary btn-block" onClick={onClick}>Edit</button>
    </Fragment>
)

const editForm = (
  <Fragment>
      <form onSubmit={onSubmit}>
          <div className="form-group">
              <label htmlFor="username">Email: </label>
              <input type='email' name='email' value={email} onChange={onChange} />
          </div>
          <div className="form-group">
              <label htmlFor="password">Firstname: </label>
              <input type='text' name='firstname' value={user.data.firstname} onChange={onChange} />
          </div>
          <div className="form-group">
              <label htmlFor="password">Lastname: </label>
              <input type='text' name='lastname' value={user.data.lastname} onChange={onChange} />
          </div>
          <input type="submit" value="Comfirm" className="btn btn-primary btn-block" />
      </form>

  </Fragment>
)
  return (
      <div className='container'>
        { !edit ? accountInfo : editForm}
      </div>
  )
}

export default Account
