import React, { Fragment, useContext, useEffect, useState } from 'react'
import AlertContext from '../../contexts/alert/alertContext';
import UserContext from '../../contexts/user/userContext';
import ProfileContext from '../../contexts/profile/profileContext';
import { Button } from 'react-native'


const EditProfile = () => {
    const  profileContext = useContext(ProfileContext);
    const  alertContext = useContext(AlertContext);
    const  userContext = useContext(UserContext);

    const { getProfile, profile, emptyProfile, error, success, clearMessage } = profileContext;
    const { setAlert } = alertContext;
    const { loadUser, user} = userContext;

    useEffect(() => {
        if(error) {
            setAlert(error, 'danger');
            clearMessage();
        }
        if(success) {
            setAlert(success, 'success');
            clearMessage();
        }
        // eslint-disable-next-line
    }, [error, success]);

    return (
        <Fragment>
            <p>Firstname:</p> <p>{user && user.data.firstname}</p> 
            <p>Lastname:</p> <p>{user && user.data.lastname}</p>
            <Button
  onPress={onPressLearnMore}
  title="Learn More"
  color="#841584"
  accessibilityLabel="Learn more about this purple button"
/>
            <form>
                <div className="form-group">
                    <label htmlFor="gender">Gender: </label>
                    <input type="radio" name="gender" value="male"/> Male
{/* 
                    <input type='radio' name='gender' 
                    placeholder={profile && profile.data.gender} 
                    onChange={e => profile.data.gender = e.target.value.toLowerCase()} /> */}
                </div>
                <div className="form-group">
                    <label htmlFor="birthday">Birthday: </label>
                    <input type='text' name='birthday'
                    placeholder={profile && profile.data.birthday}
                    onChange={e => profile.data.birthday = e.target.value.toLowerCase()} />
                </div>
                <div className="form-group">
                    <label htmlFor="lastname">Biography: </label>
                    <input type='text' name='biography'
                        placeholder={profile && profile.data.biography} 
                        onChange={e => profile.data.biography = e.target.value.toLowerCase()} />
                </div>
                <div className="form-group">
                    <label htmlFor="lastname">Interests: </label>
                    <input type='text' name='interests'
                        placeholder={profile && profile.data.interests}
                        onChange={e => profile.data.interests = e.target.value.toLowerCase()} />
                </div>
                <input type="submit" value="Comfirm" className="btn btn-primary btn-block" />
            </form>
        </Fragment>
    )
}

export default EditProfile
