import {
    REGISTER_SUCCESS,
    REGISTER_FAIL,
    CLEAR_ERROR
} from '../types';


export default (state, action) => {
    switch (action.type) {
        case REGISTER_SUCCESS:
            return{
                ...state,
                ...action.payload,
                loading: false
            };
        case REGISTER_FAIL:
            return {
                ...state,
                error: action.payload,
                loading: false
            }
        default:
            return state;
    }
}
