import React,{ Fragment, useContext, useEffect }  from 'react'
import UserContext from '../../contexts/user/userContext';
import { EDITACCOUNT } from '../../contexts/types';

const Account = () => {
    const userContext = useContext(UserContext);
    const { user, isEdit, error, success, editAccount } = userContext;

  useEffect(() => {
    userContext.loadUser();
    //eslint-disable-next-line
  }, []);

  const onClick = () => {
      editAccount();
  }
  const accountInfo = (
    <Fragment>
        <button className="btn btn-primary btn-block" onClick={onClick}>Edit</button>
          <p className="lead">Email: {user && user.data.email}</p> 
          <p className="lead">Firstname: {user && user.data.firstname}</p> 
          <p className="lead">Lastnme: {user && user.data.lastname}</p>
    </Fragment>
)
  return (
      <div className='container'>{ !isEdit && accountInfo }

      </div>
  )
}

export default Account
