  const { user, error, success, editAccount } = userContext;

  const [edit, setEdit] = useState(false);

  useEffect(() => {
    userContext.loadUser();
    //eslint-disable-next-line
  }, []);

  const onClick = () => {
      setEdit(true);
  }

