const userModel = require('../models/user');

module.exports = {
  getAccount: async (req, res) => {
    const result = await userModel.getUserInfoById(req.params.userid);

    if (result.err) {
      return res.status(400).json({
        error: result.err
      });
    } else {
      return res.status(200).json({
        data: result
      });
    }
  },
  register: async (req, res) => {
    const check_email = await userModel.verifyExistEmail(req.body.email);

    if (check_email) {
      return res.status(400).json({
        error: check_email.err
      });
    } else {
      const check_username = await userModel.verifyExistUsername(req.body.username);

      if (check_username) {
        return res.status(400).json({
          error: check_username.err
        });
      } else {
        const result = userModel.createNewUser(req.body);

        if (result.err) {
          err;
          return res.status(400).json({
            error: result.err
          });
        } else return res.status(200).json({
          message: 'Successfully register, you may log in'
        });
      }
    }
  },
  login: async (req, res) => {
    const result = await userModel.login(req.body);

    if (result.err) {
      return res.status(400).json({
        error: result.err
      });
    } else {
      return res.status(200).json({
        message: 'sucessfully login',
        data: result
      });
    }
  },
  logout: async (req, res) => {
    const result = await userModel.logout(req.body.userid);

    if (result) {
      return res.status(400).json({
        error: result.err
      });
    } else {
      return res.status(200).json({
        message: 'user offline'
      });
    }
  },
  modify_email: async (req, res) => {
    let data = req.body;
    data.userid = req.params.userid;
    const result = await userModel.modify_email(data);
    if (result) return res.status(400).json({
      error: result.err
    });else return res.status(200).json({
      message: 'Email has been successfully updated'
    });
  },
  modify_firstname: async (req, res) => {
    let data = req.body;
    data.userid = req.params.userid;
    const result = await userModel.modify_firstname(data);
    if (result) return res.status(400).json({
      error: result.err
    });else return res.status(200).json({
      message: 'Firstname has been successfully updated'
    });
  },
  modify_lastname: async (req, res) => {
    let data = req.body;
    data.userid = req.params.userid;
    const result = await userModel.modify_lastname(data);
    if (result) return res.status(400).json({
      error: result.err
    });else return res.status(200).json({
      message: 'Lastname has been successfully updated'
    });
  },
  getProfile: (req, res) => {
    userModel.getProfileInfoById(req.params.userid, (err, profile) => {
      if (err) return res.status(400).json({
        error: err
      });else {
        let result = profile[0];
        userModel.getInterestsById(req.params.userid, (err, interests) => {
          if (err) return res.status(400).json({
            error: err
          });else {
            result.interests = interests;
          }
        });

        if (req.body.visitorid != req.params.userid) {
          let data = {};
          data.id_user = req.params.userid;
          data.id_visitor = req.body.visitorid;
          userModel.visitPlusOne(data, (err, id_link) => {
            if (err) return res.status(400).json({
              error: err
            });else {
              let data = {
                notification: 'visit',
                id_link: id_link
              };
              userModel.addNotif(data, err => {
                if (err) return res.status(400).json({
                  error: err
                });
              });
            }
          });
        }

        return res.status(200).json({
          data: result
        });
      }
    });
  },
  modify_profile: (req, res) => {
    let data = req.body;
    data.id_user = req.params.userid;
    userModel.modify_profile(data, err => {
      if (err) return res.status(400).json({
        error: err
      });else return res.status(200).json({
        message: 'Profile has been successfully updated'
      });
    });
  }
};
